import requests

from .dataclasses import FilmData


class BfiAdaptor:
    def get_bfi_2022(self):
        return self.get_bfi('https://www.bfi.org.uk/sight-and-sound/greatest-films-all-time')

    def get_bfi_2022_directors(self):
        return self.get_bfi('https://www.bfi.org.uk/sight-and-sound/directors-100-greatest-films-all-time')

    def get_bfi(self, url):
        from bs4 import BeautifulSoup

        response = requests.get(url)
        soup = BeautifulSoup(response.content, 'html.parser')

        results = []
        for article in soup.find_all('article'):
            title = article.find('h1').text.strip()
            year_and_countries = article.find_all('p')[2].text.split(" ", maxsplit=1)
            year = year_and_countries[0]
            countries = year_and_countries[1].split(", ")
            director = article.find_all('p')[3].text.split('Directed by ')[1]
            results.append(FilmData(
                title=title,
                year=year,
                countries=countries,
                director=director,
            ))
        return results


